### 代码:(可能有点乱,勿喷)

```cpp
#include <bits/stdc++.h>//万能头
using namespace std;
struct tim//存储时间
{
    int h,m,sum;//sum为总分钟数
}a,b,c,d;//四个时间点
int q,p,t,aa,bb;
int GCD(int x,int y)//求最大公约数,原理相信大家都知道
{
    if(y==0)
    {
        return x;
    }
    else
    {
        return GCD(y,x%y);//经典的求法
    }
}
bool yes(int x,int y)//判断是否能同时闪烁
{
    if(y%x!=0)//如果不能整除就直接输出Never
    {
        return true;
    }
    else
    {
        return false;
    }
}
int main()//主函数
{
    scanf("%d:%d%d:%d%d:%d%d:%d",&a.h,&a.m,&b.h,&b.m,&c.h,&c.m,&d.h,&d.m);//输入数据
    a.sum=a.h*60+a.m;
    b.sum=b.h*60+b.m;
    c.sum=c.h*60+c.m;
    d.sum=d.h*60+d.m;
    //算分钟数
    if(a.sum>b.sum)
    {
        q=a.sum-b.sum;
    }
    else
    {
        q=a.sum+60*24-b.sum;
    }
    //算时间差
    p=GCD(c.sum,d.sum);//求最大公约数
    if(yes(p,q))//如果无法同时闪烁
    {
        printf("Never");
        return 0;//直接结束
    }
    //以下开始暴力求时间
    do
    {
    //看哪个小就给哪个加时间
        if(a.sum>b.sum)
        {
            b.sum+=d.sum;
        }
        else
        {
            a.sum+=c.sum;
        }
    }
    while(a.sum!=b.sum);//直到两个数相等为止
    t=a.sum/(60*24);//求天数
    a.sum%=60*24;//求近一天内的总分钟数
    t%=7;//求出星期
    switch(t)//判断星期
    {
        case 0://由于是星期六开始,t=0的时候是星期六
        printf("Saturday");
        break;
        //以下同理
        case 1:
        printf("Sunday");
        break;
        case 2:
        printf("Monday");
        break;
        case 3:
        printf("Tuesday");
        break;
        case 4:
        printf("Wednesday");
        break;
        case 5:
        printf("Thursday");
        break;
        default:
        printf("Friday");
        break;
    }
    aa=a.sum/60;//求出最终小时
    bb=a.sum%60;//求出最终分钟
    printf("\n");//别忘了换行
    if(aa<10)//小于10就加前导0
    {
        printf("0");
    }
    printf("%d:",aa);//输出小时
    if(bb<10)//同理
    {
        printf("0");
    }
    printf("%d",bb);//输出分钟
    return 0;
}
```

