    for(i=1;i<=16;i++){
        for(j=1;j<=n+1-(1<<i);j++){
            st[0][j][i]=max(st[0][j][i-1],st[0][j+(1<<i-1)][i-1]);
            st[1][j][i]=min(st[1][j][i-1],st[1][j+(1<<i-1)][i-1]);
        }
    }
    for(i=1;i<=n;i++){
        if(a[i]<a[i+1]){
            yes=1;
        }
    }
    if(!yes){
        printf("0");
        return 0;
