		if(c == 'H')
		{
			if(numh[num] == 0)
				numh[num]++, h++;
			else
				cout << "GRESKA" << endl, exit(0);
		}
		if(c == 'T')
		{
			if(numt[num] == 0)
				numt[num]++, t++;
			else
				cout << "GRESKA" << endl, exit(0);
		}
	}
```

