		f[y]=-1;
		while(q.size()!=0){
			int sum1=q.front();
			q.pop();
			if(vis[sum1]==1)continue;
			if(f[sum1]==-1){
				for(int i=h[sum1];i;i=ne[i]){
					int num=e[i];
					if(f[num]==0){
						f[num]=1;
						vis[num]=1;
						for(int i=h[num];i;i=ne[i]){//删点
							int how=e[i];
							d[how]--;
							if(d[how]==0){
								q.push(how);
							}
						}
					}
				}	
			}
			else if(out[sum1]==0){
				f[sum1]=-1;//先手不可能胜利
			}else{
				f[sum1]=-1;
				for(int i=h[sum1];i;i=ne[i]){
					int num=e[i];
					if(f[num]==0){
						f[num]=1;
						vis[num]=1;
						for(int i=h[num];i;i=ne[i]){//同样，删点
							int how=e[i];
							d[how]--;
							if(d[how]==0){
								q.push(how);
							}
						}
					}
				}				
			}
		}
