			for(int i=1;i<=cnt;i++)//枚举块内的修改
			{
				num=(s[i].a|a)&(s[i].b&b);
				if(num==int(s[i].a|a))
				{
					num=(s[i].a|a)^(s[i].b&b);
					ans+=(1<<count[num])*s[i].k;
