					if(las)
					{
						par[las] = u; par[u] = las; fp[u] = fp[las] = 1;
						las = 0;
					}
					else las = u;
					u = fa[u];
				}
				break;
			}
			fa[u] = mn[u]; int v = par[u]; fa[v] = u;
			id.push_back(v);
		}
	}
	return ans;
}
