}q[N];
bool cmp(const node &x,const node &y){
	return x.x<y.x;
}

const int B=548,D=N/B+10;
int pos[N],L[N],R[N];
ll sum[D],calc[N];
int p[N];
//标定连续1的端点，左端指向右端，右端指向左端
//因为端点只能向两边延伸，所以用不到并查集
bool b[N];
void add(int x){
	p[x]=x;
	b[x]=1;
	bool f1=b[x-1]&&(x!=L[pos[x]]),f2=b[x+1]&&(x!=R[pos[x]]);
	//左右能否相连
	if(!f1&&!f2)
		++sum[pos[x]];
	else if(f1&&f2){
		int k1=(x-1)-p[x-1]+1,k2=p[x+1]-(x+1)+1;
		sum[pos[x]]+=calc[k1+1+k2]-calc[k1]-calc[k2];
        int p1=p[x+1],p2=p[x-1];//这里有可能p[x+1]=x+1，导致一些问题
		p[p[x-1]]=p1;
		p[p[x+1]]=p2;
	}
	else if(f1){
		int k1=(x-1)-p[x-1]+1;
		sum[pos[x]]+=calc[k1+1]-calc[k1];
		p[x]=p[x-1];
		p[p[x-1]]=x;
	}
	else{
		int k2=p[x+1]-(x+1)+1;
		sum[pos[x]]+=calc[k2+1]-calc[k2];
		p[p[x+1]]=x;
		p[x]=p[x+1];
	}
}
ll ask(int l,int r){
	ll ans=0;
	if(pos[l]==pos[r]){
		int num=0;
		_for(i,l,r)
			if(b[i]) ++num;
			else ans+=calc[num],num=0;
		ans+=calc[num];
	}
	else{
		int num=0;
		_for(i,l,R[pos[l]])
			if(b[i]) ++num;
			else ans+=calc[num],num=0;
		for(Re i=pos[l]+1;i<pos[r];++i){
			if(p[L[i]]==R[i]){
				num+=R[i]-L[i]+1;
				continue;
			}
			else{
				if(!b[L[i]]){
					ans+=calc[num];
					num=0;
				}
				else{
					int k=p[L[i]]-L[i]+1;
					num+=k;
					ans+=calc[num];
					ans-=calc[k];
					num=0;
				}
				if(b[R[i]]){
					int k=R[i]-p[R[i]]+1;
					num=k;
					ans-=calc[k];
				}
				ans+=sum[i];
			}
		}
		_for(i,L[pos[r]],r)
			if(b[i]) ++num;
			else ans+=calc[num],num=0;
		ans+=calc[num];
	}
	return ans;
}
ll ans[N];
int r[N],cnt;
struct Edge{
	int t,n;
}edge[N];
il void add_edge(int u,int v){
	edge[++cnt]={v,r[u]};
	r[u]=cnt;
}

signed main(){
	int n=re(),m=re();
	_for(i,1,n){
		a[i]=re();//值域为[1,n]可以用链接表
		add_edge(a[i],i);
		pos[i]=i/B+1;
		if(pos[i]!=pos[i-1]){
			R[pos[i-1]]=i-1;
			L[pos[i]]=i;
		}
		calc[i]=(ll)i*(i+1)/2;
	}
	R[pos[n]]=n;

	_for(i,1,m){
		q[i]={re(),re(),re(),i};
	}
	sort(q+1,q+1+m,cmp);
	int now=1;
	_for(i,1,m){
		while(now<=q[i].x){
			for(Re j=r[now],y;j&&(y=edge[j].t);j=edge[j].n)
				add(y);
			++now;
		}
		ans[q[i].i]=ask(q[i].l,q[i].r);
	}
	_for(i,1,m) pr(ans[i]),pc('\n');
	return 0;
}
