				add(y,0);
			++now;
		}
		__for(j,num1,1){
			if(q1[j].i<q2[i].i&&!used[q1[j].l]){
				used[q1[j].l]=1;
				if(q1[j].x<=q2[i].x)
					add(q1[j].l,1);
			}
		}
		_for(j,1,num1)
			if(!used[q1[j].l]){
				used[q1[j].l]=1;
				if(a[q1[j].l]<=q2[i].x)
					add(q1[j].l,1);
			}
		ans[q2[i].i]=ask(q2[i].l,q2[i].r);
		del();
		_for(j,1,num1) used[q1[j].l]=0;
	}


	_for(i,1,num1)
		have[q1[i].l]=0,a[q1[i].l]=q1[i].x;
	memset(r,0,sizeof(int)*(n+1));
	memset(sum,0,sizeof(int)*(pos[n]+1));
	memset(p,0,sizeof(int)*(n+1));
	memset(b,0,sizeof(int)*(n+1));
	cnt=0;
}
bool Q[N];

signed main(){
	n=re(),m=re();
	int B=sqrt(0.81*n)+1,MB=sqrt(16.5*m)+1;
	_for(i,1,n){
		a[i]=re();
		pos[i]=i/B+1;
