			L[pos[i]]=i;
			R[pos[i-1]]=i-1;
		}
		calc[i]=(ll)i*(i+1)/2;
	}
	R[pos[n]]=n;

	for(Re i=1;;++i){
		int LL=(i-1)*MB+1,RR=i*MB;
		if(RR>m) RR=m;
