			if(op==1){
				q1[++num1]={re(),0,re(),i};
			}
			else{
				Q[i]=1;
				q2[++num2]={re(),re(),re(),i};
			}
		}
        //处理
		_for(i,1,num1)
			have[q1[i].l]=1;
		_for(i,1,n)
			if(!have[i])
				add_edge(a[i],i);
		sort(q2+1,q2+1+num2,cmp);
