				if(q1[j].i<q2[i].i&&!used[q1[j].l]){
					used[q1[j].l]=1;
					if(q1[j].x<=q2[i].x)
						add(q1[j].l,1);
				}
			}
			_for(j,1,num1)
				if(!used[q1[j].l]){
					used[q1[j].l]=1;
					if(a[q1[j].l]<=q2[i].x)
						add(q1[j].l,1);
				}
			ans[q2[i].i]=ask(q2[i].l,q2[i].r);
			while(top){//删除
				b[st[top].id]=0;
				sum[pos[st[top].id]]-=st[top].ans;
				if(st[top].pl1)
					p[st[top].pl1]=st[top].pl2;
				if(st[top].pr1)
					p[st[top].pr1]=st[top].pr2;
