			}
			_for(j,1,num1) used[q1[j].l]=0;
		}


		_for(i,1,num1)
			have[q1[i].l]=0,a[q1[i].l]=q1[i].x;
		memset(r,0,sizeof(int)*(n+1));
		memset(sum,0,sizeof(int)*(pos[n]+1));
		memset(p,0,sizeof(int)*(n+1));
		memset(b,0,sizeof(int)*(n+1));
		cnt=0;

		if(RR==m) break;
	}

	_for(i,1,m)
		if(Q[i])
			pr(ans[i]),pc('\n');
