```cpp
#include <cstdio>
#include <cstring>
#include <cmath>
#include <algorithm>

#define re register

namespace Fread
{
    const int SIZE = 1 << 23;
    char buf[SIZE], *S, *T;
    inline char getchar()
    {
        if (S == T)
        {
            T = (S = buf) + fread(buf, 1, SIZE, stdin);
            if (S == T)
                return '\n';
        }
        return *S++;
    }
}
namespace Fwrite
{
    const int SIZE = 1 << 23;
    char buf[SIZE], *S = buf, *T = buf + SIZE;
    inline void flush()
    {
        fwrite(buf, 1, S - buf, stdout);
        S = buf;
    }
    inline void putchar(char c)
    {
        *S++ = c;
        if (S == T)
            flush();
    }
    struct NTR
    {
        ~NTR()
        {
            flush();
        }
    } ztr;
}

#ifdef ONLINE_JUDGE
#define getchar Fread::getchar
#define putchar Fwrite::putchar
#endif

inline int read()
{
	re int x = 0, f = 1;
	re char c = getchar();
	while(c < '0' || c > '9')
	{
		if(c == '-') f = -1;
		c = getchar();
	}
	while(c >= '0' && c <= '9')
	{
		x = (x << 3) + (x << 1) + (c ^ 48);
		c = getchar();
	}
	return x * f;
}

inline void write(re long long x)
{
	if(x > 9)
		write(x / 10);
	putchar(x % 10 + 48);
}

inline int min(re const int &x, re const int &y)
{
	return x < y ? x : y;
}

const int N = 3e5 + 1, S = 2e3 + 1;

struct Modify
{
	int opt, pos, val;
} q1[S];

struct Query
{
	int opt, l, r, x, id;
} q2[S];

int n, m, c1, c2, a[N];

long long calc[N];

bool seq[N];

int L[S], R[S], id[N], p[N], sum[S];

inline void clear()
{
	memset(seq, 0, sizeof seq);
	memset(p, 0, sizeof p);
	memset(sum, 0, sizeof sum);
}

inline void init()
{
	re int sqn = sqrt(n * 4 / 5 + 1);
	for(re int i = 1, j, c = 1; i <= n; i = j + 1, ++c)
	{
		L[c] = i, R[c] = j = min(n, i + sqn);
		for(re int t = L[c]; t <= R[c]; ++t)
			id[t] = c;
	}
}

int top;

struct Node
{
	int id, ans, t1[4];
	bool flg;
	inline void clear()
	{
		t1[0] = t1[1] = t1[2] = t1[3] = flg = 0;
	}
} sta[S], tmp;

inline void modify(re const int &pos, re bool flg)
{
	p[pos] = pos, seq[pos] = 1, tmp.id = pos;
	re const int lst = pos - 1, nxt = pos + 1, tmp_p = p[lst];
	re const bool flg1 = (seq[lst] && L[id[pos]] != pos), flg2 = (seq[nxt] && R[id[pos]] != pos);
	if(!flg1 && !flg2)
		tmp.clear(), tmp.ans = 1;
	else
	{
		tmp.flg = 1;
		if(flg1 && flg2)
		{
			tmp.ans = (nxt - p[lst]) * (p[nxt] - lst);
			tmp.t1[0] = p[lst], tmp.t1[1] = p[p[lst]], p[p[lst]] = p[nxt];
			tmp.t1[2] = p[nxt], tmp.t1[3] = p[p[nxt]], p[p[nxt]] = tmp_p;
		}
		else
		{
			if(flg1)
			{
				tmp.ans = nxt - p[lst];
				tmp.t1[0] = pos, tmp.t1[1] = p[pos], p[pos] = p[lst];
				tmp.t1[2] = p[lst], tmp.t1[3] = p[p[lst]], p[p[lst]] = pos;
			}
			else
			{
				tmp.ans = p[nxt] - lst;
				tmp.t1[0] = pos, tmp.t1[1] = p[pos], p[pos] = p[nxt];
				tmp.t1[2] = p[nxt], tmp.t1[3] = p[p[nxt]], p[p[nxt]] = pos;
			}
		}
	}
	sum[id[pos]] += tmp.ans;
	if(flg) sta[++top] = tmp;
}

inline void back()
{
	while(top)
	{
		tmp = sta[top--], sum[id[tmp.id]] -= tmp.ans, seq[tmp.id] = 0;
		if(tmp.flg)
		{
			p[tmp.t1[2]] = tmp.t1[3];
			p[tmp.t1[0]] = tmp.t1[1];
		}
	}
}

inline long long query(const int &l, const int &r)
{
	re long long ans = 0;
	if(id[l] == id[r])
	{
		int cnt = 0;
		for(re int i = l; i <= r; ++i)
			if(seq[i])
				cnt++;
			else
				ans += calc[cnt], cnt = 0;
		return ans + calc[cnt];
	}
	re int c1 = 0, c2 = 0;
	for(re int i = l; i <= R[id[l]]; ++i)
		if(seq[i])
			c1++;
		else
			ans += calc[c1], c1 = 0;
	for(re int i = r; i >= L[id[r]]; --i)
		if(seq[i])
			c2++;
		else
			ans += calc[c2], c2 = 0;
	re int tot = c1;
	for(re int i = id[l] + 1; i <= id[r] - 1; ++i)
		if(p[L[i]] == R[i])
			tot += R[i] - L[i] + 1;
		else
		{
			if(seq[L[i]])
			{
				tot += p[L[i]] - L[i] + 1, ans -= calc[p[L[i]] - L[i] + 1];
			}
			ans += calc[tot] + sum[i], tot = 0;
			if(seq[R[i]])
			{
				tot += R[i] - p[R[i]] + 1, ans -= calc[R[i] - p[R[i]] + 1];
			}
		}
	return ans + calc[tot + c2];
}

long long ans[S];

int cnt, head[N];

struct Edge
{
	int to, nxt;
} G[N];

bool broke[N], use[N];

inline void add(const int &u, const int &v)
{
	G[++cnt] = {v, head[u]};
	head[u] = cnt;
}

inline bool cmp(const Query &x, const Query &y)
{
	return x.x < y.x;
}

inline void solve()
{
	clear();
	for(re int i = 1; i <= c1; ++i)
		broke[q1[i].pos] = 1;
	for(re int i = 1; i <= n; ++i)
		if(!broke[i])
			add(a[i], i);
	std::sort(q2 + 1, q2 + c2 + 1, cmp);
	int pot = 1;
	for(re int i = 1; i <= c2; ++i)
	{
		while(pot <= q2[i].x)
		{
			for(re int i = head[pot]; i; i = G[i].nxt)
				modify(G[i].to, 0);
			pot++;
		}
		for(re int j = c1; j >= 1; --j)
			if(q1[j].opt < q2[i].opt && !use[q1[j].pos])
			{
				use[q1[j].pos] = 1;
				if(q1[j].val <= q2[i].x)
					modify(q1[j].pos, 1);
			}
		for(re int j = 1; j <= c1; ++j)
			if(!use[q1[j].pos])
			{
				use[q1[j].pos] = 1;
				if(a[q1[j].pos] <= q2[i].x)
					modify(q1[j].pos, 1);
			}
		ans[q2[i].id] = query(q2[i].l, q2[i].r);
		back();
		for(re int j = 1; j <= c1; ++j)
			use[q1[j].pos] = 0;
	}
	cnt = 0;
	memset(head, 0, sizeof head);
	for(re int i = 1; i <= c1; ++i)
		broke[q1[i].pos] = 0;
}

signed main()
{
	n = read(), m = read();
	int sqm = sqrt(m * 11);
	init();
	for(re int i = 1; i <= n; ++i)
		a[i] = read();
	for(re int i = 1; i <= n; ++i)
		calc[i] = 1ll * i * (i + 1) / 2;
	for(re int i = 1, j; i <= m; i = j + 1)
	{
		j = min(m, i + sqm), c1 = c2 = 0;
		for(re int t = i; t <= j; ++t)
			if(read() == 1)
				q1[++c1] = {t, read(), read()};
			else
				q2[++c2] = {t, read(), read(), read(), c2};
		solve();
		for(re int t = 1; t <= c2;++t)
