{
	scanf("%d%d",&n,&l);

	for(register int i=1;i<=n;i++)
	{
		scanf("%d",&a[i]);
	}

	for(register int i=1;i<=n;i++)
	{
		scanf("%d",&st[i]);
	}

	for(register int i=1;i<=n;i++)
	{
		if(st[i]==false)
		{
			printf("%d ",a[i]);
		}
	}

	//这里要保证面向东边的蚂蚁首先到达，所以分成两个循环

	for(register int i=1;i<=n;i++)
	{
		if(st[i]==true)
