		}//now就是答案
		int trr=sa*2;
		if(trr>rr||(trr==rr&&now>tmp))rr=trr,tmp=now;
		if(!rr)pc('P'),pc('e'),pc('a'),pc('c'),pc('e'),pc('\n');
		else wrt(a[tmp].x),pc(' '),wrt(rr),pc('\n');
	}
	fwrite(wb,1,p2,stdout);
	return 0;
}
