bool check(int x)
{
	int lsum=askl(x),rsum=askr(cnt-x+1);//分别查询 a,b 的前缀和，这里用倒着维护后缀和的方式维护前缀和
	now=min(lsum,rsum);
	return lsum<=rsum;
}//二分判断
void get()
{
	int l=1,r=cnt,mid=(l+r)>>1;
	while(l<=r)
	{
		if(askr(cnt-mid+1)<now)
			r=mid-1;
