		int v=e[i].v;
		dfs2(v,bas);
		d[u]^=d[v];
	}
	if(cnt[u]%2==1)
	{
		d[u]^=1,d[fa[u][bas]]^=1;
		cnt[fa[u][bas+1]]+=cnt[u];
	}
	if((val[u]>>bas)&1)
	{
		int now=((1<<(bas+1)))-(val[u]&((1<<(bas+1))-1));
		int cur=lca(u,now);
		d[u]^=1;
		d[cur]^=1;
		cnt[fa[cur][bas]]++;
	}
	else
	{
		int now=(1<<bas)-(val[u]&((1<<(bas))-1));
		int cur=lca(u,now);
		cnt[cur]++;
	}
	if(d[u])ans+=(1<<bas);
}
int main()
{
	freopen("tree.in","r",stdin);
	freopen("tree.out","w",stdout);
	memset(p,-1,sizeof(p));
	n=getint();
	for(int i=1;i<=n;++i)val[i]=getint();
	for(int i=2;i<=n;++i)
	{
		fa[i][0]=getint();
		add(fa[i][0],i);
	}
	for(int i=2;i<=maxn*2-1;++i)pre[i]=pre[i>>1]+1; 
	dfs(1);
	dfs1(1,1);
	for(int i=0;i<=20;++i)
	{
		dfs2(1,i);
		for(int j=0;j<=n;++j)d[j]=0,cnt[j]=0;
	}
	printf("%lld\n",ans);
	return 0;
} 
```
### 一些注意事项：

我考场上倍增写错了调了半天，我是这样写的
```cpp
u=fa[u][i];
x-=(dep[u]-dep[fa[u][i]]);
```
这里我先跳祖先再减，这样是错的，因为调到最上面时会有 $dep[u]-dep[fa[u][i]]\not=(1<<i)$ 的情况。

所以要讲代码写成：
```cpp
x-=(dep[u]-dep[fa[u][i]]);
u=fa[u][i];
