#include<bits/stdc++.h>
//#pragma GCC optimize("O1,O2,O3,Ofast,no-stack-protector,unroll-loops,fast-math")
//#pragma GCC target("sse,sse2,sse3,ssse3,sse4.1,sse4.2,avx,avx2,popcnt,tune=native")
