int ans = 0 ;
int main(){
	scanf("%d",&n) ;
	for(int i=1;i<=n;i++) {
		scanf("%d",a + i);
		sum[i] = sum[i-1] + a[i];
	}
	for(int i=2;i<=n;++i) {
		ans += sum[i] > 0 ? sum[i] : 0 ;
	}
	printf("%d\n",ans) ;
	return 0 ;
} 


