    if(sz[u]>=k) ans=C[u][0][k];//注意边界情况，防止 RE。
    for(int i=u;fa[i];i=fa[i]){
        int tmp=Dis(fa[i],u);if(tmp>k || (k-tmp)>sz[fa[i]]) continue;
        ans+=C[fa[i]][0][k-tmp];
        if(sz[i]>=k-tmp) ans-=C[i][1][k-tmp];
    }
    return ans;
}

