    while(ch>='0'&&ch<='9'){
        x=(x<<1)+(x<<3)+(ch^48);
        ch=getchar();
    }
    return x*f;
}
const int N=2e5+10;
const int B=1e5;
struct node{int id,dis;};
struct query{int y,id;};
vector <query> Q[N];
int edge[N];int ans[N];
int head[N];int edgecnt;
int Next[N];
int max(int x,int y){
	if(x>y)return x;
	else return y;
}
void add(int u,int v){
	Next[++edgecnt]=head[u];
	edge[edgecnt]=v;
	head[u]=edgecnt;
}
