bool cmp(node a,node b){return a.id<b.id;}
int n,m,u,v,w;int root;int bucket[B];
int s[N];int x,y;
bool vis[N];int nowmin=-1;int value[N];
int rt;int now;
int Root[N];
void init(int u,int fa){
	s[u]=1;
	for(int i=head[u];i;i=Next[i]){
		int v=edge[i];
		if(v==fa||vis[v])continue;
		init(v,u);s[u]+=s[v];
	}
}
void find(int u,int fa){
	if(fa==0)nowmin=1e9,rt=u;
	int MAX=-1;
	for(int i=head[u];i;i=Next[i]){
		int v=edge[i];
		if(v==fa||vis[v])continue;
		find(v,u);MAX=max(MAX,s[v]);
	}
	MAX=max(MAX,s[rt]-s[u]);
	if(nowmin>MAX){
		nowmin=MAX;
		root=u;
	}
}
int deep[N];
void getdeep(int u,int fa){
	bucket[now]++;deep[u]=now;
	for(int i=head[u];i;i=Next[i]){
		int v=edge[i];
		if(v==fa||vis[v])continue;
		now+=1;getdeep(v,u);now-=1;
	}
}
void calc(int u,int fa){
	for(int i=0;i<Q[u].size();i++){
		int y=Q[u][i].y;int id=Q[u][i].id;
		y-=deep[u];if(y<0)continue;
		ans[id]+=bucket[y];
	}
	for(int i=head[u];i;i=Next[i]){
		int v=edge[i];
		if(v==fa||vis[v])continue;
		calc(v,u);
	}
}
void remove(int u,int fa,int p){
	bucket[deep[u]]+=p;
	for(int i=head[u];i;i=Next[i]){
		int v=edge[i];
		if(v==fa||vis[v])continue;
		remove(v,u,p);
	}
}
void solve(int u){
	vis[u]=true;
	now=0;getdeep(u,0);
	for(int i=0;i<Q[u].size();i++){
		int y=Q[u][i].y;int id=Q[u][i].id;
		y-=deep[u];if(y<0)continue;
		ans[id]+=bucket[y];
	}
	for(int i=head[u];i;i=Next[i]){
		int v=edge[i];
		if(vis[v])continue;
		remove(v,0,-1);calc(v,0);remove(v,0,1);
	}
	remove(u,0,-1);
	for(int i=head[u];i;i=Next[i]){
		int v=edge[i];
		if(vis[v])continue;
		init(v,0);
		find(v,0);
		solve(root);
	}
}
int T; 
int main(){
	T=read();
	while(T--){
		n=read();m=read();
		for(int i=1;i<=n;i++)Q[i].clear();
		rt=0;now=0;nowmin=-1;root=0;edgecnt=0;
		memset(Next,0,sizeof(Next));
		memset(edge,0,sizeof(edge));
		memset(head,0,sizeof(head));
		memset(vis,false,sizeof(vis));
		memset(ans,0,sizeof(ans));
		memset(bucket,0,sizeof(bucket));
		for(int i=1;i<n;i++){
			u=read();v=read();
			add(u,v);add(v,u);
		}
		for(int i=1;i<=m;i++){
			x=read();y=read();
			Q[x].push_back((query){y,i});
		}
		init(1,0);
		find(1,0);
		solve(root);
		for(int i=1;i<=m;i++){
			printf("%d\n",ans[i]);
		}
	}
	return 0;
}
