#define eps 1e-8
const int inf=0x3f3f3f3f;
const int Maxn=4e5+10;
struct segmenttree{
	struct segtree{
		int sum;
		int tag;
	}t[Maxn<<2];
	#define ls node<<1
	#define rs node<<1|1
	void pushup(int node)
	{
		t[node].sum=t[ls].sum^t[rs].sum;
	}
	void pushdown(int node,int l,int r)
	{
		if(t[node].tag)
		{
			int mid=(l+r)>>1,len;
			// left
			len=mid-l+1;
			t[ls].tag^=t[node].tag;
			t[ls].sum^=(len%2)*t[node].tag;

			// right
			len=r-mid;
			t[rs].tag^=t[node].tag;
			t[rs].sum^=(len%2)*t[node].tag;

			t[node].tag=0;
		}
	}
	void update(int node,int l,int r,int ql,int qr,int val)
	{
		if(ql<=l && r<=qr)
		{
			t[node].sum^=((r-l+1)%2)*val;
			t[node].tag^=val;
			return ;
		}
		int mid=(l+r)>>1;
		pushdown(node,l,r);
		if(ql<=mid)
		{
			update(ls,l,mid,ql,qr,val);
		}
		if(qr>mid)
		{
			update(rs,mid+1,r,ql,qr,val);
		}
		pushup(node);
	}
	int query(int node,int l,int r,int pos)
	{
		if(l==r)
		{
			return t[node].sum;
		}
		int mid=(l+r)>>1;
		pushdown(node,l,r);
		if(pos<=mid)
		{
			return query(ls,l,mid,pos);
		}else{
			return query(rs,mid+1,r,pos);
		}
	}
}seg;
struct Queris{
