map<int,int> mp;
void LSH()
{
	for(int i=1;i<=totnum;i++) tmp2[i]=tmp[i];
	sort(tmp+1,tmp+1+totnum);
	int newn=unique(tmp+1,tmp+1+totnum)-tmp-1;
	Lens=newn;
	for(int i=1;i<=totnum;i++)
	{
		int x=tmp2[i];
		int y=lower_bound(tmp+1,tmp+1+newn,tmp2[i])-tmp;
		mp[x]=y;
	}
}
int main()
