```cpp
#include<bits/stdc++.h>
using namespace std;
const int N=1e5+5;
char sa[N],sb[N];
int a[N],b[N];
int main()
{
	int T;
	scanf("%d",&T);
	while(T--)
	{
		int k;
		scanf("%s%s%d",sa,sb,&k);
	    int n=strlen(sa);
		int p=-1,cnt=0;
	    for(int i=0;i<n;i++)a[i]=sa[i]^48,b[i]=sb[i]^48;
	    for(int i=0;i<n;i++)
		{
	        if(b[i])
			{
	            int pre=cnt+(a[i]>=b[i]);
	            if (pre<=k&&pre+n-i-1+(a[i]<b[i]&&b[i]>1)>=k)p=i;
	        }
	        cnt+=(a[i]!=b[i]);
	    }
	    if(p<0)
	    {
	    	printf("-1\n");
	    	continue;
		}
		cnt=0;
	    for(int i=0;i<p;i++)
			cnt+=(a[i]!=b[i]),a[i]=b[i];
	    if (a[p]>=b[p])
			a[p]=b[p]-1,cnt++;
	    else if(a[p]!=b[p]-1&&cnt<k) 
			a[p]=b[p]-1,cnt++;
	    for(int i=p+1;i<n;i++)
	    	if(a[i]<9&&cnt<k)
				a[i]=9,cnt++;
	    for(int i=n-1;i>p;i--)
	        if(sa[i]=='9'&&cnt<k) 
				a[i]=8,cnt++;
	    while(cnt<k)cnt++,a[p]--;
	    for (int i=0;i<n;i++) 
			putchar(a[i]^48);
		putchar('\n');		
	}
	return 0;
}
```
