#include <cstdio>
#include <iostream>
#include <algorithm>
#include <cstring>
using namespace std;
struct people
{
	string name;
	int h,m,s;
	int f;
	int ac;
}k[1005];
template <typename T>
inline void read(T &s)
{
	s = 0;
	T w = 1;
	char ch = getchar();
	while(ch < '0'||ch > '9')
	{
		if(ch == '-')
		w = -1;
		ch = getchar();
