					return a.name < b.name;
				else
					return a.s < b.s;
			}
			else
				return a.m < b.m;
		}
		else
			return a.h < b.h;
	}
	else
		return a.ac > b.ac;
}
int main()
{
	int n,m;
	read(n);
	read(m);
	int wz;
	for(int i = 1;i <= n;i++)
	{
		string a;
		cin >> a;
		int j = 0;
		while(j < a.size() && a[j] != ' ')
		{
			k[i].name += a[j];
			j++;
		}
		if(k[i].name == "NijeZivotJedanACM")
		{
			wz = i;
			for(int z = 1;z <= m;z++)
			{
				cin >> a;
			}
			continue;
		}
		for(int z = 1;z <= m;z++)
		{
			cin >> a;
			if(a[0] != '-')//不是负号就当正号做
			{
				k[i].ac++;
				j++;
				int q = a[1] - '0';
				k[i].f += q;
				k[i].h += (a[3] - '0') * 10 + a[4] - '0';
				k[i].m += (a[6] - '0') * 10 + a[7] - '0';
				k[i].s += (a[9] - '0') * 10 + a[10] - '0';
				while(k[i].s >= 60)
				{
					k[i].s -= 60;
					k[i].m++;
				}
				k[i].m += q * 20;
				while(k[i].m >= 60)
				{
					k[i].m -= 60;
					k[i].h++;
				}
			}
		}
	}
	string a;
	cin >> a;
	for(int i = 1;i <= m;i++)
	{
		cin >> a;
		if(a[0] != '-')
		{
			k[wz].ac++;
			int q = a[1] - '0';
			k[wz].f += q;
			k[wz].h += (a[3] - '0') * 10 + a[4] - '0';
			k[wz].m += (a[6] - '0') * 10 + a[7] - '0';
			k[wz].s += (a[9] - '0') * 10 + a[10] - '0';
			while(k[wz].s >= 60)
			{
				k[wz].s -= 60;
				k[wz].m++;
			}
			k[wz].m += q * 20;
			while(k[wz].m >= 60)
			{
				k[wz].m -= 60;
				k[wz].h++;
			}
		}
	}
	sort(k+1,k+1+n,cmp);
	for(int i = 1;i <= n;i++)
	{
		if(k[i].name == "NijeZivotJedanACM")
		{
			printf("%d",i);
			return 0;
		}
	}
	return 0;
}
