```cpp
#include<bits/stdc++.h>
#define ll long long
using namespace std;

struct zz{
	string a[1005];
	string NM;
}t[1005],now;

struct ss{
	ll T;
	int tot;
	string NM;
	void clear(){
		T=0;
		tot=0;
	}
}end[1005];
bool cmp(ss x,ss y){
	if(x.tot!=y.tot)
		return x.tot>y.tot;
	if(x.T!=y.T)
		return x.T<y.T;
	return x.NM<y.NM;
}

int n,m;

int main(){
//	freopen("acm.in","r",stdin);
