	for(int i=1;i<=m;i++){
		cin>>now.a[i];
	}
	for(int i=1;i<=n;i++){
		if(t[i].NM==now.NM)
			t[i]=now;
		end[i].NM=t[i].NM;
		for(int j=1;j<=m;j++){
			if(t[i].a[j][0]=='-')
				continue;
			end[i].tot++;
			end[i].T+=(t[i].a[j][1]-'0'-1)*20*60;
			end[i].T+=(t[i].a[j][3]-'0')*10*60*60;
			end[i].T+=(t[i].a[j][4]-'0')*60*60;
