由于 Indjy 大佬放了个 DFS，我就来放个 BFS，BFS 的细节见代码：
```cpp
#include<bits/stdc++.h>
#define MOD 998244353
using namespace std;
int head[10000005],cnt,n,cc_cnt,cc[100000005],color[10000005];
struct Edge{
	int u,v,w,next;
}e[50000005];
bool flag=0;
struct Node{
	int w,now;
	inline bool operator <(const Node &x)const
	{
		return w>x.w;
	} 
}; 
inline void add(int u,int v,int w)
{
    e[++cnt].u=u;
    e[cnt].v=v;
    e[cnt].w=w;
    e[cnt].next=head[u];
    head[u]=cnt;
}  
bool vis[2132144];
long long red=0,blue=0;
void BFS_color(int num,int Color){
	//这里我是用的BFS染色，opt=1就要求两点颜色相同，否则该颜色不同
	color[num]=Color;
	if(color[num]==1)
	red++;
	else
	blue++;
	queue<int>q;
	q.push(num);
	vis[num]=1;
	while(q.size()){
		int top=q.front();
		q.pop(); 
		vis[top]=1;
		for(int i=head[top];i;i=e[i].next){
			int tv=e[i].v;
			if(color[tv]==color[top]&&e[i].w==0){
				flag=1;
				return ;
			}
			if(color[tv]+color[top]==3&&e[i].w==1){
				flag=1;
				return ;
			}
			if(vis[tv])
			continue;
            //注意这个判断的位置，不能放在前面
			if(e[i].w==1){
				color[tv]=color[top];
				if(color[tv]==1)
				red++;
				else
				blue++;
			}
			else{
				color[tv]=3-color[top];
				if(color[tv]==1)
				red++;
				else
				blue++;
			}
			q.push(tv);
			vis[tv]=1;
		}
	}
}
int main()
{
	long long ans=1,ans1=0,ans2=0;
	cin>>n;
	for(int i=1;i<=n;i++){
		int u,v;
		cin>>u>>v;
		add(i,u,v);
		add(u,i,v);
	}
	for(int i=1;i<=n;i++){
		if(vis[i]==0){
			red=blue=0;
			cc[i]=++cc_cnt;
			flag=0;
			BFS_color(i,1);
			if(flag){//只要有一个无解的连通块就一定无解
				cout<<"No answer"<<endl;
				return 0;
			}
			else{
				ans*=2;
				ans%=MOD;
				ans1+=max(red,blue);
				ans2+=min(red,blue);
			}
		}
	}
	cout<<ans<<endl<<ans1<<endl<<ans2<<endl;
    return 0;
}
