}

const int N=55, M=505, K=205;
const ll infll=0x3f3f3f3f3f3f3f3fll;

int n, m, T, k, c[N];
int e, hd[N], to[M<<1], nxt[M<<1], val[M<<1];
struct Fst{int t, x, y;}p[K];

inline bool cmp(const Fst& a, const Fst& b){
