	nxt[e]=hd[u], hd[u]=e;
}

namespace Task1{
	const int MX=53005;

	ll f[MX][N];
	int g[MX][N];

	int main(){
		FOR(i, 1, k) g[p[i].t][p[i].x]=p[i].y;

		FOR(i, 0, T) FOR(u, 1, n) f[i][u]=-infll;
		f[0][1]=0;
		FOR(i, 0, T)
			FOR(u, 1, n) if(f[i][u]>-infll){
				f[i][u]+=1ll*g[i][u]+1ll*c[u];
				REP(j, u) if(i+val[j]<=T)
					cmax(f[i+val[j]][v], f[i][u]);
			}

