}

inline bool spj2(){
	if(m!=n) return false;
	FOR(i, 1, n) if(to[hd[i]]!=i%n+1)
		return false;
	return true;
}
namespace Task2{

	int w[N], s[N], tot;
	ll ans, res;

	int main(){
		FOR(i, 1, n) tot+=(w[i]=val[hd[i]]);
		if(T%tot) return 0*puts("-1");
		FOR(i, 1, n) s[i]=s[i-1]+w[i-1];

		FOR(i, 1, n) res+=c[i];
		ans=res*(T/tot)+c[1];
		FOR(i, 1, k) if(p[i].t%tot==s[p[i].x])
			ans+=p[i].y;

		printf("%lld\n", ans);

		return 0;
	}
}

namespace Task3{
	const int D=255;
	int d, mx, lg;
	ll f[D];
	struct Matrix{
		ll a[D][D];
		inline ll* operator[](int x){
			return a[x];
		}
		inline void init(int flag=0){
			FOR(i, 1, d) FOR(j, 1, d)
				a[i][j]=-infll;
			if(flag) FOR(i, 1, d) a[i][i]=0;
		}
	}E, Ep[30];
	inline void mul(Matrix& a, Matrix& b, Matrix& c){
		static Matrix A, B;
		A=a, B=b;
		c.init();
		FOR(i, 1, d) FOR(j, 1, d) FOR(k, 1, d)
			cmax(c[i][k], A[i][j]+B[j][k]);
	}
	inline void mul(Matrix& a, ll *b, ll *c){
		static ll B[D];
		FOR(i, 1, d) B[i]=b[i];
		FOR(i, 1, d) c[i]=-infll;
		FOR(i, 1, d) FOR(j, 1, d)
			cmax(c[i], a[i][j]+B[j]);
	}
	inline void init(){
		FOR(i, 1, e) mx=max(mx, val[i]);
		d=n*mx;
		E.init();
		FOR(u, 1, n) FOF(j, 1, mx)
			E[(u-1)*mx+j][(u-1)*mx+j+1]=0;
		FOR(u, 1, n) FOR(i, 1, mx){
			int p=(u-1)*mx+i;
			REP(j, u) if(i+val[j]-1<=mx){
				int q=(v-1)*mx+i+val[j]-1;
				E[q][p]=c[v];
			}
		}
		Ep[0]=E;
		while((1<<(lg+1))<=T) ++lg;
		FOR(i, 1, lg) mul(Ep[i-1], Ep[i-1], Ep[i]);
		FOR(i, 1, d) f[i]=-infll;
		f[mx]=c[1];
	}
	inline void qpow(int x){
		for(int i=0; x; x>>=1, ++i) if(x&1)
			mul(Ep[i], f, f);
	}
	inline void work(){
		sort(p+1, p+k+1, cmp);
		int cur=0;
		FOR(i, 1, k){
			int t=p[i].t, x=p[i].x, y=p[i].y;
			qpow(t-cur);
			f[x*mx]+=y;
			cur=p[i].t;
		}
		if(cur<T) qpow(T-cur);
	}
	int main(){
		init();
		work();
		printf("%lld\n", f[mx]>=0?f[mx]:-1);
		return 0;
	}
}

int main(){
