for (int i = 1; i <= m; i++) 
	scanf ("%lld%lld%lld", &e[i].from, &e[i].to, &e[i].w);
for (ll i = 1, tim, u, v; i <= k; i++)
	scanf ("%lld%lld%lld", &tim, &u, &v),TouShui[tim][u] = v; 
for (int i = 1; i <= T; i++)
{
	for (int j = 1; j <= m; j++)
		if(f[i - e[j].w][e[j].from])
		{
			f[i][e[j].to] = max(f[i][e[j].to], f[i - e[j].w][e[j].from]) + c[e[j].to] + TouShui[i][e[j].to];   
		}
} 
printf("%lld", f[T][1]);
