	for (i = 1; i < n; ++i) x = read(),y = read(),adde(x,y);
	dfs(1);
	for (i = 1; i <= n; ++i) lim[i] = 0;
	m = read();
	while (m--) x = read(),y = read(),lim[y] = max(lim[y],dpt[x]);
	dp(1);
	cout << ans << '\n';
}
