			p->l->sum = p->l->sum * p->mul % mod;
			p->l->mul = p->l->mul * p->mul % mod;
		}
		if (p->r) {
			p->r->sum = p->r->sum * p->mul % mod;
			p->r->mul = p->r->mul * p->mul % mod;
