			while(now<=n&&a[now].h<=T[i].h){
				update(a[now].x,a[now].h);
				now++;
			}
			ll val=ask(T[i].l,T[i].r,T[i].h);
			out[T[i].id]+=T[i].opt*val;
		}
	}
}

