  for(int k=1;k<=bel[n];k++){
    for(int i=1;i<=n;i++) sum[k][i]+=sum[k][i-1];
  }
  for(int i=1;i<=bel[n];i++){
    for(int j=1;j<i;j++){
      for(int k=1;k<=len[i];k++){
	f[i][j][k]=sum[j][b[st[i]+k-1]]+f[i][j][k-1];
