				memset(used,0,sizeof(bool)*(n2+1));
				while(1){
					int mx=0,mn=1e9,mxid=0,mnid=0;
					_for(i,1,n2)
						if(!used[i]){
							if(mx<a2[i].d) mx=a2[i].d,mxid=i;
							if(mn>=a2[i].d) mn=a2[i].d,mnid=i;
