    A[i]=p[i-1]; 
    for(i=1;i<=n;i++)
    {
        res=-1;
        if(!num[A[i]+k]) continue;
        if(k==0)
        {
            if(num[A[i]]>=2)
            res=A[i]*num[A[i]];
        }
        else
        res=min(num[A[i]],num[A[i]+k])*(A[i]+A[i]+k);
