#define ll long long
#define uint unsigned int

using namespace std;
int rd() {
	int f=1,sum=0; char ch=getchar();
	while(!isdigit(ch)) {if(ch=='-') f=-1;ch=getchar();}
	while(isdigit(ch)) {sum=(sum<<3)+(sum<<1)+ch-'0';ch=getchar();}
	return sum*f;
}
uint urd() {
	uint sum=0; char ch=getchar();
	while(!isdigit(ch)) {ch=getchar();}
	while(isdigit(ch)) {sum=(sum<<3)+(sum<<1)+ch-'0';ch=getchar();}
	return sum;
}
ll lrd() {
	ll f=1,sum=0; char ch=getchar();
	while(!isdigit(ch)) {if(ch=='-') f=-1;ch=getchar();}
	while(isdigit(ch)) {sum=(sum<<3)+(sum<<1)+ch-'0';ch=getchar();}
	return sum*f;
}
void pr(uint x) {
	if(x<0) {putchar('-');x=-x;}
	if(x>9) pr(x/10);
	putchar(x%10+'0');
}

uint fpow(uint x,uint y,uint mod) {
	uint res=1; x%=mod;
//	cout<<x<<" "<<y<<endl;
	while(y) {
		if(y&1) res=1llu*res*x%mod;
		x=1llu*x*x%mod; y>>=1;
	}
//	cout<<res<<endl;
	return res;
}

#define N (int)(5e5+5)

bool vis[N];
int pri[N>>1],a[N],tot;
void init() {
	for(int i=2;i<N;i++) {
		if(!vis[i]) pri[++tot]=i;
		for(int j=1;j<=tot&&i*pri[j]<N;j++) {
			vis[i*pri[j]]=1;
			if(i%pri[j]==0) break;
		}
	}
	for(int i=1;i<N;i++) a[i]=1;
	for(int i=1;i<=tot;i++)
		for(int j=1;j*pri[i]<N;j++)
			a[j*pri[i]]+=a[j];
	for(int i=1;i<N;i++) a[i]+=a[i-1];
}

uint H(uint n,uint mod) {
	if(n<N) return a[n];
	uint ans=0;
	for(uint l=1,r;l<=n;l=r+1) {
		r=n/(n/l);
		ans+=(r-l+1)*(n/l);
		ans%=mod;
	}
	return ans;
}

int main() {
	init();
	uint n=urd(),mod=urd();
	uint ans=1;
	for(uint l=1,r;l<=n;l=r+1) {
		r=n/(n/l);
		ans=(1llu*ans*fpow(1llu*l*l%mod*fpow(1llu*(r+1)*(r+1)%mod,mod-2,mod)%mod,H(n/l,mod-1)%mod,mod))%mod;
	}
	pr(ans);
	return 0;
}
