		//cout<<2<<" "<<xval[2]<<" "<<fib[2]<<" "<<val[2]<<endl;
		while(fib[x]<=n) {
			val[x]=val[x-1]+val[x-2];
			if(rfib[x-2]<=k) val[x]++;
			xval[x]=xval[x-1]+val[x-1];
			if(rfib[x]<=k) xval[x]++;
			//cout<<x<<" "<<xval[x]<<" "<<fib[x]<<" "<<val[x]<<endl;
			x++;
		}
		dfs(fib[x-1],x-1);
		if(fib[x-1]<=n) cout<<xval[x-1]+sum<<endl;
		else cout<<xval[1]<<endl;
	}
	return 0;
} 
