vector <int> v;
inline int solve(vector <int> v ,int i) {
 	if (v.size() == 0) return 0;
	if (i == 0) return v.size();
	vector <int> v0 ,v1;
	for (auto x : v) {
		if (x >> i & 1) v1.push_back(x);
		else v0.push_back(x);
	}
	if (k >> i & 1)
		return max(v0.size() + solve(v1 ,i - 1) ,v1.size() + solve(v0 ,i - 1));
	else
		return max(solve(v0 ,i - 1) ,solve(v1 ,i - 1));
}
