	n = read(), k = read();
	for (int i = 1; i <= n; i++) a[i] = read();
	sort(a+1, a+1+n);
	for (R int x = 0; x <= (1<<20); x++){
		R int sum = 0, l = 1, r = n, now = 0;
		for (R int i = 20; i >= 0; i--){
			R int pos = lower_bound(a+l, a+r+1, now|(1<<i))-a;
			if (!((k>>i)&1)){
				if ((x>>i)&1) l = pos, now |= (1<<i);//1^1==0，1在右边，向右走
