#include<bits/stdc++.h>
using namespace std;
typedef long long LL;
const long long INF=3e18;
struct node{
    int nxt,to,w;
}edge[5000010];
int temp[1000010];
int head[1000010];
int n,m,a,b,c,op,idx,num;
int vis[1000010];
int w[1010][1010];
int star1[4],star2[4];
LL dis[1000010][3];
LL ans=2*INF,id;
void add(int u,int v,int w)
{
    edge[++idx].nxt=head[u];
    edge[idx].to=v;
    edge[idx].w=w;
    head[u]=idx;
}
priority_queue<pair<LL,int> > q;
void dijkstra(int s,int cnt)//cnt=1时存的是闪电的单源最短路， 
{
    for(int i=1;i<=n*m;i++)//初始化 
    {
        vis[i]=0;
    }
    q.push(make_pair(0,s));
    ++op;
    dis[s][cnt]=w[star1[op]][star2[op]];
    while(q.size())
    {
        int x=q.top().second;
        q.pop();
        if(vis[x])
        continue;
        vis[x]=1;
        for(int i=head[x];i;i=edge[i].nxt)
        {
            int y=edge[i].to;
            if(dis[x][cnt]+edge[i].w<dis[y][cnt])
            {
                dis[y][cnt]=dis[x][cnt]+edge[i].w;
                q.push(make_pair(-dis[y][cnt],y)); 
            }
        }
    }
}
int main()
{
    cin>>n>>m>>a>>b>>c;
    star1[1]=n,star1[2]=1,star1[3]=1;
    star2[1]=a,star2[2]=b,star2[3]=c;
    for(int i=n;i>=1;i--)
    {
        for(int j=1;j<=m;j++)
        {
            scanf("%d",&w[i][j]);
            temp[++id]=w[i][j];
        }
    }
    int i=n,j=1;
    num=1;
    while(1)//先存横向边 
    {
        if(j>=m)//j==m时,j+1溢出,换到下一行
        {
            if(i==1)//已经是最后一行了 
            break;
            i--;
            j=1;
            num++;//5到6之间没边 
        } 
        add(num,num+1,w[i][j+1]);//1->2 w=8 
        add(num+1,num,w[i][j]);//2->1 w=1
        j++;
        num++;
    }
    i=n,j=1,num=1;
    while(1)//再存竖向边
    {
        if(j>m)
        {
            if(i==2)
            break;
            i--;
            j=1;
        }
        add(num,num+m,w[i-1][j]);//i=5,j=1,,i=4,j=1
        add(num+m,num,w[i][j]);//i=5,j=1
        j++;
        num++;
    }
    int st1=a,st2=(n-1)*m+b,st3=(n-1)*m+c;//算出起点坐标对应点的编号
    for(int i=1;i<=n*m;i++)//总点数 
    {
        for(int j=1;j<=3;j++)
        {
            dis[i][j]=INF;
        }
    }
    dijkstra(st1,0);
    dijkstra(st2,1);
    dijkstra(st3,2);
    for(int i=1;i<=n*m;i++)
    {
        ans=min(ans,dis[i][0]+dis[i][1]+dis[i][2]-2*temp[i]);
    }
    cout<<ans;
    return 0;
}

