		}
		b=0;
		for(rg int j=0;j<tnc[i];++j)
		{
			if(pc[i][j])
			{
				while(b<tnd[i]&&td[i][b].id==-1)++b;
				if(b==tnd[i])
				{
					ans[tc[i][j].id]=-1,ansv-=c;
					continue;
				}
				ans[tc[i][j].id]=td[i][b].id,ansv+=td[i][b].val-c,++b;
			}
		}
	}
	printf("%lld\n",ansv);
	for(rg int i=1;i<=m;++i)print(ans[i]),putchar('\n');
	return 0;
}
