```cpp
#include<iostream>
using namespace std;
int a[2005][2005],f[2005][2005],g[2005][2005],under[2005][2005],right[2005][2005],ans=999999,MAX;
//数据范围是2000，所以就开2005了，ans是最后更新答案用的，所以赋一个比较大的值
int main(){
	int n,m;//输入就不说了
	scanf("%d%d",&n,&m);
	for(int i=1;i<=n;i++){
		for(int j=1;j<=m;j++){
			scanf("%d",&a[i][j]);
		}
	}//从前往后和从后往前算一次
	for(int i=1;i<=n;i++){
		for(int j=1;j<=m;j++){
			f[i][j]=max(f[i-1][j],f[i][j-1])+a[i][j];
		}
	}
	for(int i=n;i>=1;i--){
		for(int j=m;j>=1;j--){
			g[i][j]=max(g[i+1][j],g[i][j+1])+a[i][j];
		}
	}//下方的情况和右边的情况
	for(int i=1;i<=n;i++){
		for(int j=1;j<=m;j++){
			under[i][j]=max(under[i][j-1],f[i][j]+g[i+1][j]);
		}
	} 
	for(int i=1;i<=n;i++){
		for(int j=1;j<=m;j++){
			right[i][j]=max(right[i-1][j],f[i][j]+g[i][j+1]);
		}
	}
  	MAX=1;//给这个临时变量赋一个值
	for(int i=n;i>=1;i--){
     	for(int j=m;j>=1;j--){
       		if(i-1>=0){//这两个是边界情况
       			MAX=max(MAX,right[i-1][j]);
			}
       		if(j-1>=0){
       			MAX=max(MAX,under[i][j-1]);
			}
       		MAX=max(MAX,max(f[i-1][j],f[i][j-1])+max(g[i][j+1],g[i+1][j]));//算出一个值并和ans进行比较
       		ans=min(ans,MAX);
     	}
   	}
   	printf("%d\n",ans);
	return 0; 
}
