	char c;
	int ans=0;
	int n;
	cin>>n;
	while(n){
		c=getchar();
		if(c>='A'&&c<='Z'){
			bool pd=1;
			c=getchar();
			while(c!=' '&&c!='.'&&c!='?'&&c!='!'){
