#include<cstring>
#include<iostream>
#include<cstdio>
using namespace std;
char s[2000],n,lens;
bool flag;
int ans[1000],dot;
int main(){
    scanf("%d",&n);
    while(dot<n){
        scanf("%s",s);
        int lens=strlen(s);
        flag=0;
        if(s[lens-1]=='.'||s[lens-1]=='?'||s[lens-1]=='!') lens--;
        if(s[0]<'A'||s[0]>'Z') flag=1;
        for(int i=1;i<lens;i++){
            if(s[i]<'a'||s[i]>'z') flag=1;
        }
        if(!flag) ans[dot+1]++;
        if(s[lens]=='.'||s[lens]=='?'||s[lens]=='!'){
            dot++;
        }
    }
    for(int i=1;i<=n;i++) printf("%d\n",ans[i]);
}
