#include<bits/stdc++.h>
#define int long long
using namespace std;
int T,a,b;
int check2(int k,int x){
	return x%10+(x/10%10)*k+(x/100)*k*k;
}
int check1(int x,int k)
{
	int now,tmp;
	now=0,tmp=1;
	while(x)
	{
		if(x%k>=10) return -1;
		now+=tmp*(x%k);
		x/=k;
		tmp*=10;
	}
	return now;
}
signed main()
{
	int ans=10;
	scanf("%lld%lld",&a,&b);
	for(int i=b;i<=1000;i++)
	{
		int l=10,r=1e18;
		if(i>=100) r=1e9;
		while(l<r)
		{
			int mid=(l+r)>>1;
			int qwq=check2(mid,i); 
			if(qwq>=a) r=mid;
			//else if(qwq==2) {l=mid;break;}
			else l=mid+1; 
		}
		if(check2(l,i)==a) ans=max(ans,l);
	}
	for(int i=1000000;i>=ans;i--)
		if(check1(a,i)>=b) {ans=i;break;}
	cout<<ans<<endl;
	return 0;
}
